// Colour variables used in this paragraph
$d-p-banner-text-color: $color-odysseus !default;
$d-p-banner-background-overlay: $color-cassandra !default;
$d-p-banner-line-color: $color-odysseus !default;

// Margins, paddings and sizes.
$d-p-banner-text-line-height: 1.7 !default;
$d-p-banner-button-margin-top: 4.375rem !default;
$d-p-banner-padding-top: 18.5% !default;
$d-p-banner-padding-bottom: 14.5% !default;
$d-p-banner-mobile-padding-top: 6.25rem !default;
$d-p-banner-mobile-padding-bottom: 6.25rem !default
$d-p-banner-line-margin-top: $line-center-margin-top !default;;
$d-p-banner-line-margin-bottom: $line-center-margin-bottom !default;;

// Other settings
$d-p-banner-img-opacity: 0.5 !default;

.d-p-banner {
  background: $d-p-banner-background-overlay;
  color: $d-p-banner-text-color;
  z-index: 0;
  h2 {
    @include font-main-header-big;
  }
  h3.field--name-field-d-subtitle {
    @include font-subtitle;
    text-align: center;
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-banner-line-color;
      margin-top: $d-p-banner-line-margin-top;
      margin-bottom: $d-p-banner-line-margin-bottom;
    }
  }
  p {
    @include font-body-standard;
    text-align: center;
    line-height: $d-p-banner-text-line-height;
  }
  .field--type-link {
    text-align: center;
    a {
      @extend .btn;
      @extend .btn-primary;
      margin-top: $d-p-banner-button-margin-top;
    }
  }
  .backstretch-item {
    img {
      opacity: $d-p-banner-img-opacity;
      @include media-breakpoint-down(sm) {
        max-width: none;
        width: 100%;
        overflow: hidden;
      }
    }
  }
  .content-wrapper {
    width: 100%;
    padding-top: $d-p-banner-padding-top;
    padding-bottom: $d-p-banner-padding-bottom;
    @include media-breakpoint-down(sm) {
      padding-top: $d-p-banner-mobile-padding-top;
      padding-bottom: $d-p-banner-mobile-padding-bottom;
    }
  }
}

// Colour variables used in this paragraph
$d-p-subscribe-color: $color-odysseus !default;
$d-p-subscribe-background: $color-agamemnon !default;
$d-p-subscribe-line-color: $line-background !default;

// Padding, margins & sizing.
$d-p-subscribe-form-margin: 2.2rem auto 1.25rem auto !default;
$d-p-subscribe-form-max-width: 56rem !default;
$d-p-subscribe-mobile-form-max-width: 31.25rem !default;
$d-p-subscribe-mobile-form-margin-y: 3% !default;
$d-p-subscribe-padding-y: 8% !default;
$d-p-subscribe-line-margin-top: 1.45rem !default;
$d-p-subscribe-line-margin-bottom: $line-center-margin-bottom !default;

// Other settings.
$d-p-subscribe-img-opacity: 0.4 !default;

// Subscribe form
.d-p-subscribe-file-form {

  margin: $d-p-subscribe-form-margin;
  text-align: center;

  .form-control {
    border: none;
  }

  @include make-inline-form;
  fieldset.form-item-name {
    max-width: 0.25 * $d-p-subscribe-form-max-width;
    margin-right: 0.025 * $d-p-subscribe-form-max-width;
  }
  fieldset.form-item-mail {
    max-width: 0.4 * $d-p-subscribe-form-max-width;
    margin-right: 0.025 * $d-p-subscribe-form-max-width;
  }
  @include media-breakpoint-down(lg) {
    fieldset.form-item-mail {
      max-width: 0.35 * $d-p-subscribe-form-max-width;
    }
  }
  @include media-breakpoint-down(md) {
    max-width: $d-p-subscribe-mobile-form-max-width;
    margin: $d-p-subscribe-mobile-form-margin-y auto;
    fieldset.form-item-name, fieldset.form-item-mail {
      max-width: 100%;
      margin-right: 0;
    }
  }
}

// Entire paragraph
.subscribe_file {
  color: $d-p-subscribe-color;
  z-index: 0;
  padding: $d-p-subscribe-padding-y 0;
  background: $d-p-subscribe-background;
  .backstretch-item {
    img {
      opacity: $d-p-subscribe-img-opacity;
    }
  }
  a {
    color: $d-p-subscribe-color;
  }
  h2 {
    @include font-main-header;

    // Horizontal line
    &:after {
      @include decoration-header-line-main-center;
      margin-top: $d-p-subscribe-line-margin-top;
      margin-bottom: $d-p-subscribe-line-margin-bottom;
      color: $d-p-subscribe-line-color;
    }
  }
  p {
    text-align: center;
  }
}

// Colour variables used in this paragraph.
$d-p-single-box-text-color: $color-hecuba !default;
$d-p-single-box-line-color: $color-agamemnon !default;
$d-p-text-blocks-line-color: $color-agamemnon !default;
$d-p-text-blocks-background: $color-agamemnon !default;

// Margins, paddings and sizing.
$d-p-text-blocks-group-margin: 7.75rem 0 5.5rem 0 !default;
$d-p-text-blocks-mobile-group-margin: 4rem 0 2.8rem 0 !default;
$d-p-text-blocks-group-line-margin: 0.625rem auto !default;
$d-p-single-box-line-margin-top: $line-left-margin-top !default;
$d-p-single-box-line-margin-bottom: $line-left-margin-bottom !default;
$d-p-text-blocks-group-button-margin: 3.125rem auto 9rem auto !default;
$d-p-text-blocks-mobile-group-button-margin: 1.875rem auto 4.375rem auto !default;
$d-p-single-box-margin-bottom: 15% !default;

// Other settings.
$d-p-single-text-box-cta-text-transform: uppercase !default;

// Font mixins
@mixin font-title-single-box {
  font-size: $font-size-lg;
  font-weight: bold;
  text-align: left;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-mdlg;
  }
}

// Group of text blocks with header and CTA
.d-p-group-of-text-blocks {
  & > h2 {
    @include font-main-header;
    margin: $d-p-text-blocks-group-margin;
    @include media-breakpoint-down(md) {
      margin: $d-p-text-blocks-mobile-group-margin;
    }
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-text-blocks-line-color;
      margin: $d-p-text-blocks-group-line-margin;
    }
  }
  > .field--type-link {
    text-align: center;
    a {
      @extend .btn;
      @extend .btn-primary;
      margin: $d-p-text-blocks-group-button-margin;
      @include media-breakpoint-down(md) {
        margin: $d-p-text-blocks-mobile-group-button-margin;
      }
    }
  }
}

// Single box
.d-p-text-block {
  margin-bottom: $d-p-single-box-margin-bottom;
  h2 {
    @include font-title-single-box;
    &:after {
      @include decoration-header-line;
      background: $d-p-single-box-line-color;
      margin-top: $d-p-single-box-line-margin-top;
      margin-bottom: $d-p-single-box-line-margin-bottom;
    }
  }
  p {
    @include font-body-standard;
    color: $d-p-single-box-text-color;
  }
  a {
    @include text-transform($d-p-single-text-box-cta-text-transform)

  }
}

// Colour variables used in this paragraph
$d-p-text-paged-subtitle-color: $color-agamemnon !default;
$d-p-text-paged-line-color: $color-agamemnon !default;
$d-p-text-paged-color: $color-hecuba !default;

// Margins, paddings and sizing.
$d-p-text-paged-title-margin-top: 5% !default;
$d-p-text-paged-subtitle-margin-y: 1.125rem !default;
$d-p-text-paged-line-margin-top: 1rem !default;
$d-p-text-paged-line-margin-bottom: 1rem !default;
$d-p-text-paged-margin-y: 5% !default;

.text-page {
  h2 {
    margin-top: $d-p-text-paged-title-margin-top;
  }
  h3.field--name-field-d-subtitle {
    margin: $d-p-text-paged-subtitle-margin-y 0;
    @include font-subtitle;
    text-align: center;
    color: $d-p-text-paged-subtitle-color;
    &:after {
      @include decoration-header-line-main-center;
      background: $d-p-text-paged-line-color;
      margin-top: $d-p-text-paged-line-margin-top;
      margin-bottom: $d-p-text-paged-line-margin-bottom;
    }
  }
  p {
    color: $d-p-text-paged-color;
  }
  margin-top: $d-p-text-paged-margin-y;
  margin-bottom: $d-p-text-paged-margin-y;
}

// Colour variables used in this component.
$line-background: $color-nestor !default;

// Margins, paddings and sizings.
$line-left-margin-top: 1.25rem !default;
$line-left-margin-bottom: 0.625rem !default;
$line-center-margin-top: 1.375rem !default;
$line-center-margin-bottom: 1.5rem !default;
$line-width: 3.125rem !default;
$line-thickness: 0.125rem !default;

// Header underline aligned to the left
@mixin decoration-header-line {
    content: ' ';
    display: block;
    width: $line-width;
    background: $line-background;
    position: relative;
    height: $line-thickness;
    margin-top: $line-left-margin-top;
    margin-bottom: $line-left-margin-bottom;
}

// Header underline aligned to the center
@mixin decoration-header-line-main-center {
    content: ' ';
    margin: $line-center-margin-top auto $line-center-margin-bottom auto;
    display: block;
    width: $line-width;
    background: $line-background;
    position: relative;
    height: $line-thickness;
}

// Colour variables used used in this component.
$form-control-border-color: $color-hecuba !default;

// Margins, paddings and sizes.
$form-label-margin-bottom: .4rem !default;
$form-label-font-size: .85em !default;
$form-control-padding: 1rem 1.5rem 0.8rem 1.5rem !default;
$form-control-border-thickness: 0.063rem !default;
$form-control-border-radius: 0 !default;
$form-text-margin-top: 0.25rem !default;
$form-email-margin-top: 0.25rem !default;

label {
  margin-bottom: $form-label-margin-bottom;
  font-size: $form-label-font-size;
}
.form-control {
  padding: $form-control-padding;
  border: $form-control-border-thickness solid $form-control-border-color;
  border-radius: $form-control-border-radius;
}

.form-text {
  display: block;
  margin-top: $form-text-margin-top;
}

.form-email {
  display: block;
  margin-top: $form-email-margin-top;
}
input {
  font-weight: bold;
}
input::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  font-weight: normal;
}
input::-moz-placeholder { /* Firefox 19+ */
  font-weight: normal;
}
input:-ms-input-placeholder { /* IE 10+ */
  font-weight: normal;
}
input:-moz-placeholder { /* Firefox 18- */
  font-weight: normal;
}

@mixin make-inline-form {
  fieldset {
    display: inline-block;
    input {
      margin-top: 0;
    }
  }
  @include media-breakpoint-down(md) {
    display: block;
  }
  .button {
    display: inline-block;
    vertical-align: top;
    @include media-breakpoint-down(md) {
      display: block;
      margin: 0 auto;
    }
    @include media-breakpoint-down(xs) {
      width: 100%;
    }
  }
}

// Colour variables used in this paragraph.
$d-p-side-image-banner-background-color: $color-agamemnon !default;
$d-p-side-image-banner-background-color-secondary: darken($d-p-side-image-banner-background-color, 15%) !default;
$d-p-side-image-banner-text-color: $color-odysseus !default;
$d-p-side-image-banner-link-color: $color-nestor !default;
$d-p-side-image-banner-link-hover-color: darken($d-p-side-image-banner-link-color, 5%) !default;
$d-p-side-image-line-color: $color-nestor !default;

// Margins, paddings and sizing.
$d-p-side-image-line-margin-top: 1.44rem !default;
$d-p-side-image-line-margin-bottom: 1.44rem !default;
$d-p-side-image-mobile-height: 18.75rem !default;
$d-p-side-image-banner-text-padding: 7% !default;
$d-p-side-image-banner-text-width: 35.625rem !default;
$d-p-side-image-banner-half-padding: 5% !default; // Spacing between content and half of the screen

.d-p-side-image-banner {
  color: $d-p-side-image-banner-text-color;
  h2 {
    @include font-main-header;
    text-align: left;
    &:after {
      @include decoration-header-line;
      margin-top: $d-p-side-image-line-margin-top;
      margin-bottom: $d-p-side-image-line-margin-bottom;
      margin-background: $d-p-side-image-line-color;
    }
  }
  a {
    color: $d-p-side-image-banner-link-color;
    &:hover {
      color: $d-p-side-image-banner-link-hover-color;
    }
  }
  .d-image {
    padding: 0;
    @include media-breakpoint-down(sm) {
      height: $d-p-side-image-mobile-height !important;
    }
  }
  .container-half {
    padding: $d-p-side-image-banner-text-padding;
  }
  .image-side {
    &-right {
      background: $d-p-side-image-banner-background-color;
    }
    &-left {
      background: $d-p-side-image-banner-background-color-secondary;
    }
  }

  @include media-breakpoint-up(md) {
    .container-half {
      margin-right: auto;
      margin-left: auto;
      padding-right: ($grid-gutter-width / 2);
      padding-left: ($grid-gutter-width / 2);
      width: $d-p-side-image-banner-text-width;
    }
    .image-side {
      &-right {
        .d-image {
          float: right;
        }
        .container-half {
          padding-right: $d-p-side-image-banner-half-padding;
          float: right;
        }
      }
      &-left {
        .d-image {
          float: left;
        }
        .container-half {
          padding-left: $d-p-side-image-banner-half-padding;
          float: left;
        }
      }
    }
  }
}

// Colour variables used in this component.
$lang-dropdown-header-color: $menu-main-link-color !default;  // Color of language link in main menu
$lang-dropdown-toggle-color: $menu-main-link-dropdown !default; // Color of language dropdown toggle icon
$lang-dropdown-submenu-color: $submenu-link-color !default; // Color of link in language menu
$lang-dropdown-submenu-hover-color: $submenu-link-hover-color !default; // Color of hover link in language submenu
$lang-dropdown-submenu-hover-background: $submenu-link-hover-background !default; // Color of hover bg in language submenu
$lang-dropdown-submenu-active-color: $menu-main-link-active-color !default; // Color of active link in language submenu
$lang-dropdown-mobile-background: $color-troilus !default; // Background color of mobile language menu
$lang-dropdown-mobile-link-color: $color-priam !default; // Link color of mobile language menu
$lang-dropdown-mobile-link-hover-color: lighten($lang-dropdown-mobile-link-color, 5%) !default; // Link hover color of mobile language menu
$lang-dropdown-mobile-header-color: $color-odysseus !default; // Color of active language in mobile lang menu

// Font sizes.
$lang-dropdown-link-font-size: $menu-font-size !default; // Font size of language link in main menu
$lang-dropdown-size: $menu-dropdown-size !default; // Size of dropdown icon in lang menu
$lang-dropdown-submenu-font-size: $submenu-font-size !default; // Font size in the lang submenu

// Paddings and margins.
$lang-dropdown-margin-left: 4.5rem !default;
$lang-dropdown-link-padding-x: $submenu-link-padding-x !default;
$lang-dropdown-link-height: $menu-main-link-height !default;
$lang-dropdown-dropdown-width: $menu-dropdown-width !default;  // Width of desktop dropdown
$lang-dropdown-submenu-link-height: $submenu-link-height !default;
$lang-dropdown-mobile-link-padding: 0.625rem !default;
$lang-dropdown-mobile-padding: 1.25rem !default;

.block-language.dropdown {

  // Styling for desktop (>= LG)
  @include media-breakpoint-up(lg) {
    margin-left: $lang-dropdown-margin-left;

    // Toggle - current language with dropdown
    .dropdown-toggle {
      text-transform: uppercase;
      text-align: right;
      vertical-align: middle;
      cursor: pointer;
      line-height: $lang-dropdown-link-height;  // Same height as menus.
      height: $lang-dropdown-link-height;
      font-size: $lang-dropdown-link-font-size;
      color: $lang-dropdown-header-color;
      position: relative;
      padding-right: $lang-dropdown-dropdown-width;
      &:after {
        text-align: center;
        color: $lang-dropdown-toggle-color;
        font-family: "fontello";
        content: "\e804";
        font-size: $lang-dropdown-size;
        display: block;
        position: absolute;
        top: 0;
        right: 0;
        width: $lang-dropdown-dropdown-width;
        height: auto;
        margin: 0;
        vertical-align: middle;
        border: none;
      }
      &-down {
        &:after {
          content: "\e806";
        }
      }
    }
    .dropdown-menu {
      cursor: pointer;
      padding: 0;
      left: auto;
      right: -0.063rem;
      top: ($lang-dropdown-link-height / 2) - 1.6rem;
      border-radius: 0;

      // Submenu forced to show on hover
      &.force-show {
        display: block;
      }

      li {
        list-style-type: none; // Leave for IE compatibility
        list-style-image: url(data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7); // Leave for IE compatibility (css hack)
        &:first-child, a {
          text-transform: uppercase;
          padding: 0 $lang-dropdown-link-padding-x;
          line-height: $lang-dropdown-submenu-link-height;
          display: block;
        }
        &:nth-child(1):after {
          content: "\e806";
          color: $lang-dropdown-toggle-color;
          font-family: "fontello";
          display: inline-block;
          width: 0;
          border: none;
          font-size: $lang-dropdown-size;
          margin-right: $lang-dropdown-link-padding-x;
          float: right;
        }
        a {
          font-size: $lang-dropdown-submenu-font-size;
          color: $lang-dropdown-submenu-color;
          text-transform: uppercase;
          &:hover, &:focus {
            color: $lang-dropdown-submenu-hover-color;
            background: $lang-dropdown-submenu-hover-background;
            text-decoration: none;
          }
          &:active {
            color: $lang-dropdown-submenu-active-color;
          }
        }
      }
    }
  }

  // Styling for mobile (<= MD)
  @include media-breakpoint-down(md) {
    .active-lang-code {
      display: none;
    }
    .dropdown-menu {
      position: relative;
      top: 100%;
      left: 0;
      z-index: 200;
      display: block;
      float: none;
      padding: $lang-dropdown-mobile-padding;
      margin: 0;
      text-align: center;
      width: 100%;
      background-color: $lang-dropdown-mobile-background;
      border: 0;
      border-radius: 0;
      li {
        text-transform: uppercase;
        &:first-child {
          color: $lang-dropdown-mobile-header-color;
          padding: $lang-dropdown-mobile-link-padding;
        }
        a {
          display: block;
          color: $lang-dropdown-mobile-link-color;
          padding: $lang-dropdown-mobile-link-padding;
          &:hover {
            color: $lang-dropdown-mobile-link-hover-color;
          }
        }
      }
    }
  }
}

// Paddings and margins.
$button-min-width: 18rem !default; // Minimum width of the button (desktop)
$button-mobile-min-width: 16rem !default;  // Minimum width of the button (mobile)

// Standard Bootstrap button
.btn {
  @include font-button;
  min-width: $button-min-width;
  cursor: pointer;

  @include media-breakpoint-down(sm) {
    min-width: $button-mobile-min-width;
  }
}

// Colour variables used in this component
$menu-main-link-color: $color-priam !default;
$menu-main-link-hover-color: darken($color-priam, 20%) !default;
$menu-main-link-active-color: darken($color-priam, 20%) !default;
$menu-main-link-dropdown: $color-hector !default; // Dropdown icon

$submenu-link-color: $menu-main-link-color !default; // Submenu item text color
$submenu-link-hover-color: darken($color-priam, 20%) !default; // Submenu item hover color
$submenu-link-hover-background: $color-andromache !default; // Submenu item hover color

$menu-mobile-background: $color-agamemnon !default;
$menu-mobile-link-color: $color-odysseus !default;
$menu-mobile-link-border: $color-menelaus !default;
$submenu-mobile-background: $menu-mobile-link-border !default;

$navbar-toggler-color: $color-agamemnon !default;

// Font sizes
$menu-font-size: $font-size-lg !default;
$menu-mobile-font-size: $font-size-lg !default;
$submenu-font-size: $font-size-base !default;
$menu-dropdown-size: 2.3rem !default; // Size of the dropdown arrow
$navbar-toggler-size: 2.3rem !default; // Size of the toggler arrow

// Paddings, margins and heights.
$menu-dropdown-width: 4rem !default;  // Width of desktop dropdown icon (desktop)
$menu-dropdown-mobile-padding-x: 1rem !default;  // Padding on both sides of dropdown icon (mobile)
$menu-main-link-height: $header-height - (2 * $navbar-padding-y) !default;  // Height of menu item
$menu-main-link-padding-x: 1.5rem !default; // Space on the right and left side of the link (desktop)
$navbar-toggler-width: 5.625rem !default; // Width of mobile menu on/off toggler
$navbar-toggler-height: $header-mobile-height !default; // Height of mobile menu on/off toggler
$menu-mobile-main-link-height: 3.5rem !default;  // Height of menu item (mobile)
$menu-mobile-main-link-padding-x: 2.15rem !default; // Percentage width of menu item, it should align with the toggler icon (mobile).
$submenu-offset-left: -1rem !default; // Move the submenu left or right (desktop)
$submenu-link-padding-x: 1.4rem !default; // Horizontal padding of submenu element (desktop)
$submenu-link-height: 2.9rem !default; // Height of links in submenu (desktop)
$submenu-mobile-link-height: $menu-mobile-main-link-height !default; // Height of links in submenu (mobile)

// The main menu region for >= LG
@include media-breakpoint-up(lg) {
  .region-primary-menu {
    height: $menu-main-link-height;
  }
}

// The main menu
.main-menu {

  // Menu link for >= LG
  @include media-breakpoint-up(lg) {
    &.navbar-nav {

      // Menu <a> link (not submenu)
      .nav-link {
        font-size: $menu-font-size;
        color: $menu-main-link-color;

        // Remember about Bootstrap link padding.
        line-height: $menu-main-link-height - (2 * $nav-link-padding-y);
        height: $menu-main-link-height;
        padding-left: $menu-main-link-padding-x;
        padding-right: $menu-main-link-padding-x;

        &:hover, &:focus {
          color: $menu-main-link-hover-color;
        }
        &:active {
          color: $menu-main-link-active-color;
        }
      }

      // Menu <li> item with opened dropdown.
      .menu-item--expanded {

        padding-right: $menu-dropdown-width;

        a.nav-link {
          padding-right: 0;
        }

        // Menu item with expanded submenu.
        .show {
          a.nav-link {
            &:focus, &:active {
              outline: none;
              -moz-outline-style: none
            }
          }
        }

        // Dropdown icon
        .menu-dropdown-icon {
          line-height: $menu-main-link-height - (2 * $nav-link-padding-y);
          top: $nav-link-padding-y;
          padding: 0;
          width: $menu-dropdown-width;
        }
      }

      // Submenu
      .dropdown-menu {
        padding: 0;
        margin-top: -1 * ($menu-main-link-height / 5);
        margin-left: $submenu-offset-left;
        border-radius: 0;

        // Submenu forced to show on hover
        &.force-show {
          display: block;
        }

        // Submenu item
        .dropdown-item {
          padding: 0;
          &:active, &:hover, &.active:active, &.active:hover {
            background: $submenu-link-hover-background;
            a {
              color: $submenu-link-hover-color;
              text-decoration: none;
            }
          }
          &.active {
            background: transparent;
          }
          a {
            color: $submenu-link-color;
            font-size: $submenu-font-size;
            padding: 0 $submenu-link-padding-x;
            line-height: $submenu-link-height;
            width: 100%;
            display: block;
          }
        }
      }
    }
  }

  // Menu link for <= MD
  @include media-breakpoint-down(md) {
    &.navbar-nav {
      background: $menu-mobile-background;

      // Menu link (not submenu)
      .nav-link {
        color: $menu-mobile-link-color;
        font-size: $menu-mobile-font-size;

        //width: $menu-mobile-main-link-width;

        border-bottom: 0.125rem solid $menu-mobile-link-border;
        margin: 0 $menu-mobile-main-link-padding-x;
        padding: 0;
        line-height: $menu-mobile-main-link-height;
        max-height: $menu-mobile-main-link-height;
        text-align: center;
      }

      // Menu link with dropdown
      .menu-item--expanded {
        a.nav-link {
          position: relative;

          &:after {
            // Position the icon as absolute
            line-height: $menu-mobile-main-link-height;
            max-height: $menu-mobile-main-link-height;
            float: none;
            position: absolute;
            right: 0;
            top: 0;
          }
        }
        a.menu-dropdown-icon {
          //right: (100% - $menu-mobile-main-link-width) / 2;
          right: $menu-mobile-main-link-padding-x - $menu-dropdown-mobile-padding-x;
          padding: 0 $menu-dropdown-mobile-padding-x;
        }
        &.show > a {
            border-bottom: none;
        }
      }

      // Last menu item
      .nav-item:last-child {
        .nav-link {
          border-bottom: none;
        }
      }
    }

    // Dropdown menu
    .dropdown-menu {
      border: none;
      padding: 0;

      // Single <li>
      .dropdown-item {
        padding: 0;
        background: $submenu-mobile-background;
        list-style-type: none; // Leave for IE compatibility
        list-style-image: url(data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///yH5BAEAAAAALAAAAAABAAEAAAIBRAA7); // Leave for IE compatibility (css hack)

        a {
          font-size: $menu-mobile-font-size;
          color: $menu-mobile-link-color;
          border-bottom: 0.125rem solid $menu-mobile-background;
          text-align: center;
          line-height: $submenu-mobile-link-height;
          max-height: $submenu-mobile-link-height;
          margin: 0 $menu-mobile-main-link-padding-x;
          padding: 0;
          display: block;

          &:hover, &:active, &:focus, .active {
            text-decoration: none;
          }
        }

        &:last-child a {
          border-bottom: none;
        }
      }
    }
  }

  // Menu <li> with dropdown
  .menu-item--expanded {

    position: relative;

    // Dropdown icon
    .menu-dropdown-icon {
      color: $menu-main-link-dropdown;
      font-family: "fontello";
      font-size: $menu-dropdown-size;
      display: block;
      position: absolute;
      right: 0;
      top: 0;
      text-align: center;

      &:before {
        content: "\e804";
      }

      &:hover {
        text-decoration: none;
      }
    }

    // Expanded submenu
     &.show, &.force-show {
       .menu-dropdown-icon:before {
         content: "\e806";
       }
     }
  }
}

button.navbar-toggler {
  display: none;
}
@include media-breakpoint-down(md) {
  button.navbar-toggler {
    display: block;
    z-index: 100;
    width: $navbar-toggler-width;
    height: $navbar-toggler-height;
    padding: 0;
    border: none;
    border-radius: none;

    .navbar-toggler-icon {
      width: $navbar-toggler-width;
      height: $navbar-toggler-height;

      &:after {
        font-family: "fontello";
        color: $navbar-toggler-color;
        font-size: $navbar-toggler-size;
        line-height: $navbar-toggler-height;
        position: relative;
      }
    }

    &[aria-expanded="false"] .navbar-toggler-icon:after {
      content: "\f008";
    }
    &[aria-expanded="true"] .navbar-toggler-icon {
      background: $menu-mobile-background;

      &:after {
        color: $menu-main-link-dropdown;
        content: "\e806";
        font-size: $menu-dropdown-size;
      }
    }
  }
}

@import 'layout/header';
@import 'layout/footer';
@import 'layout/download_page';

// Colour variables used in this file.
$light-gray-background: $color-hector !default;

// Paddings, margins and heights.

#main {
  margin-top: 0;
  .node__content {
    margin-top: 0;
  }
}
.page-wrapper {
  min-width: $page-min-width;
  img {
    max-width: 100%;
  }
  main.main-content {
    width: 100%;
  }
  h1 {
    @include font-main-header-big;
  }
  h2 {
    @include font-main-header;
  }
  p {
    @include font-body-standard;
  }
  a:not(.btn) {
    @include font-links;
  }
}
.no-padding {
  padding: 0;
  @include media-breakpoint-down(sm) {
    &-sm {
      padding: 0;
    }
  }
}

.alert {
  @extend .container;
}

.background {
  &-gray-light {
    background: $light-gray-background;
  }
}

body {
  -webkit-font-smoothing: antialiased;
}

// Paddings, margins and heights.
$logo-mobile-height: $header-mobile-height !default;
$logo-width: 20rem !default;
$logo-mobile-width: 12rem !default;
$logo-mobile-padding-left: 1rem !default;
$logo-md-width: 13.5rem !default;


header {
  .site-logo img {
    max-width: 100%;
    max-height: $header-height - (2 * $navbar-padding-y);
  }
  .region-primary-menu {
    .navbar {
      padding: $navbar-padding-y 0;
    }
    #block-mainnavigation, #block-languageswitcher {
      display: inline-block;
      @include media-breakpoint-down(md) {
        display: block;
      }
    }
  }
  .navbar-brand {
    max-width: $logo-width;
  }
  @include media-breakpoint-down(lg) {
    .navbar-brand {
      max-width: $logo-md-width;
    }
  }
  @include media-breakpoint-down(md) {
    min-height: $header-mobile-height;

    .navbar-brand {
      top: 0;
      z-index: 1;
      max-width: $logo-mobile-width;
      padding-left: $logo-mobile-padding-left;

      .site-logo img {
        max-width: 100%;
        width: auto;
        max-height: $header-mobile-height - (2 * $navbar-brand-padding-y);

        &.logo-svg {
          height: 100%;
        }
      }
    }
    .navbar {
      width: 100%;
      padding: 0;
    }
  }
}

// Colour variables used in this file.
$footer-background: $color-troilus !default;
$footer-text-color: $white !default;
$footer-droopler-color: $color-priam !default;
$footer-droopler-color-hover: darken($color-priam, 20%) !default;

// Paddings, margins and heights.

// Other variables.
$footer-droopler-opacity: 0.7 !default;
$footer-droopler-margin: 0.625rem auto !default;

@mixin font-footer-header {
  font-size: $font-size-vlg;
  text-transform: uppercase;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-mdlg;
  }
}
@mixin font-footer-body {
  font-size: $font-size-mdsm;
  @include media-breakpoint-down(sm) {
    font-size: $font-size-sm;
  }
}


footer {
  &.site-footer {
    font-family: $font-family-base;
  }
  background: $footer-background;
  color: $footer-text-color;
  .region-footer-main {
    h2 {
      @include font-footer-header;
      color: $footer-text-color;
    }
    p {
      @include font-footer-body;
      &:last-child {
        margin-bottom: 0;
      }
    }
    .block {
      text-align: center;
      border: 0;
    }
  }
}

.footer-droopler {
  opacity: $footer-droopler-opacity;
  color: lighten($menu-main-link-color, 20%);
  margin: $footer-droopler-margin;
  text-align: right;
  font-size: $font-size-svsm;
  @extend .container;
  a {
    font-size: $font-size-svsm;
    color: $footer-droopler-color;
    text-transform: uppercase;
    &:hover, &:focus {
      color: $footer-droopler-color-hover;
    }
  }
}
